mapper.select:
 select max(b.rz - f.fsltdz) as cfsltdz from st_stbprp_b a left join ( select p.stcd,p.rz,p.rwptn,p.w,p.blrz,p.inq,p.otq,p.tm from ( select stcd,max(tm) as tm from st_rsvr_r where tm >= to_date(#{bgTm},'yyyy-mm-dd hh:mi:ss') and tm < to_date(#{endTm},'yyyy-mm-dd hh:mi:ss') group by stcd ) r left join st_rsvr_r p on r.stcd = p.stcd and r.tm = p.tm ) b on a.stcd = b.stcd left join ( select n.stcd,n.fsltdz from st_rsvrfsr_b n,( select stcd,max(bgmd) as bgmd from st_rsvrfsr_b where #{md} >= bgmd and edmd >= #{md} group by stcd ) rn where n.stcd = rn.stcd and n.bgmd = rn.bgmd and #{md} >= n.bgmd and n.edmd >= #{md} union select p.stcd,p.fsltdz from st_rsvrfsr_b p,( select m.stcd,max(m.bgmd) as bgmd from st_rsvrfsr_b m where m.stcd not in ( select n.stcd from st_rsvrfsr_b n where #{md} >= n.bgmd and n.edmd >= #{md} ) group by m.stcd ) q where p.stcd = q.stcd and p.bgmd = q.bgmd ) f on a.stcd = f.stcd 
where a.stcd in
